svg.script:

var dolphin = document.getElementById("dolphin");
dolphin.start = 0;
dolphin.end = 1;

function animateDolphin() {
  dolphin.start += dolphin.end;
  dolphin.end = parseFloat(dolphin.start) + 6; // duration of the animation sequence
}

setInterval(animateDolphin, 7000); // adjust interval to match your desired pace
